import React, { createContext, useContext, useReducer, ReactNode } from 'react';
import { UserProfile, StrategyConfig, Account } from '../data/types';

interface AppState {
  userProfile: UserProfile | null;
  currentStrategy: StrategyConfig | null;
  accounts: Account[];
}

type AppAction = 
  | { type: 'SET_USER_PROFILE'; payload: UserProfile }
  | { type: 'SET_CURRENT_STRATEGY'; payload: StrategyConfig }
  | { type: 'SET_ACCOUNTS'; payload: Account[] }
  | { type: 'UPDATE_ACCOUNT'; payload: Account }
  | { type: 'RESET_STATE' };

const initialState: AppState = {
  userProfile: null,
  currentStrategy: null,
  accounts: []
};

function appStateReducer(state: AppState, action: AppAction): AppState {
  switch (action.type) {
    case 'SET_USER_PROFILE':
      return { ...state, userProfile: action.payload };
    case 'SET_CURRENT_STRATEGY':
      return { ...state, currentStrategy: action.payload };
    case 'SET_ACCOUNTS':
      return { ...state, accounts: action.payload };
    case 'UPDATE_ACCOUNT':
      return {
        ...state,
        accounts: state.accounts.map(account =>
          account.id === action.payload.id ? action.payload : account
        )
      };
    case 'RESET_STATE':
      return initialState;
    default:
      return state;
  }
}

interface AppStateContextType {
  state: AppState;
  dispatch: React.Dispatch<AppAction>;
}

const AppStateContext = createContext<AppStateContextType | undefined>(undefined);

export function AppStateProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(appStateReducer, initialState);

  return (
    <AppStateContext.Provider value={{ state, dispatch }}>
      {children}
    </AppStateContext.Provider>
  );
}

export function useAppState() {
  const context = useContext(AppStateContext);
  if (context === undefined) {
    throw new Error('useAppState must be used within an AppStateProvider');
  }
  return context;
}
