import React, { useState } from 'react';
import { onboardingCopy } from '../copy';
import PlaidLinkButton from '../../../components/PlaidLinkButton';
import Sheet from '../../../components/Sheet';
import { ConnectChoose } from './ConnectChoose';
import { useAppState } from '../../../state/AppStateContext';

interface ConnectProps {
  onNext: () => void;
  onBack: () => void;
}

export function Connect({ onNext, onBack }: ConnectProps) {
  const [open, setOpen] = useState(false);
  const { setAccounts } = useAppState();
  
  const addLinked = (arr: any[]) => setAccounts(prev => [...prev, ...arr]);
  
  const handlePlaidSuccess = (linked: any[]) => {
    addLinked(linked);
    console.log('Linked accounts:', linked);
    onNext();
  };

  return (
    <div className="onboarding-step">
      <h2>Let's connect your accounts</h2>
      
      <div className="connect-options">
        <PlaidLinkButton onSuccess={handlePlaidSuccess} />
        
        <button className="connect-button secondary" onClick={() => setOpen(true)}>
          Add accounts another way
        </button>
        
        <button className="connect-button secondary" onClick={onNext}>
          Skip for now
        </button>
      </div>
      
      <p className="connect-disclaimer">
        Connecting is optional. Guidewell provides educational scenarios and plain-language explanations, not financial advice.
      </p>

      <div className="onboarding-actions">
        <div className="action-buttons">
          <button className="action-button secondary" onClick={onBack}>
            Back
          </button>
          <button className="action-button secondary" onClick={onNext}>
            Skip for now
          </button>
        </div>
      </div>
      
      <p className="disclaimer">{onboardingCopy.disclaimer}</p>
      
      <Sheet open={open} onClose={() => setOpen(false)} title="Add accounts another way">
        <ConnectChoose 
          onClose={() => setOpen(false)} 
          onComplete={() => { 
            setOpen(false); 
            onNext(); 
          }} 
        />
      </Sheet>
    </div>
  );
}
